import { Directive, ElementRef, HostListener, Input } from '@angular/core';

@Directive({
  selector: '[appInputMask]'
})
export class InputMaskDirective {
  maskPattern: string = '\\d{3}-\\d{3}-\\d{4}';
  constructor(private el: ElementRef) {}

  @HostListener('input', ['$event']) onInput(event: InputEvent): void {
    debugger
    const input = this.el.nativeElement as HTMLInputElement;
    const value = input.value;

    // Apply the phone number mask
    const maskedValue = this.applyPhoneNumberMask(value);

    input.value = maskedValue;
  }

  private applyPhoneNumberMask(value: string): string {
    // Implement your phone number mask logic here
    // For example, you can format it as (XXX) XXX-XXXX
    // This is just a simple example:
    const cleaned = value.replace(/\D/g, ''); // Remove non-digit characters
    //const formatted = `(${cleaned.slice(0, 3)}) ${cleaned.slice(3, 6)}-${cleaned.slice(6, 10)}`;
    const formatted = this.applyMask(cleaned)
    return formatted;
  }



  applyMask(value: string): string {
    debugger
    if (!this.maskPattern) {
      // If no mask pattern is provided, return the original value
      return value;
    }
    const regex = new RegExp(this.maskPattern, 'g');
    const formattedValue = value.replace(regex, (match, group1, group2, group3) => {
      // Customize this logic based on your mask pattern
      return `${group1}-${group2}-${group3}`;
    });
    return formattedValue;
  }
}



