  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Customized-Masked-Input';
  phoneMaskPattern = '(000) 000-0000'
}
<input type="text" [mask]="phoneMaskPattern" />
