import { Component, Input } from '@angular/core';
import { Pattern } from './input-mask.model';

@Component({
  selector: 'input-mask',
  templateUrl: './input-mask.component.html',
  styleUrls: ['./input-mask.component.css']
})
export class InputMaskComponent {
  @Input() maskPattern!: Pattern;

  customPatterns = { '0': { pattern: new RegExp('\[a-zA-Z\]')} };
}
<input type="text" [mask]="maskPattern" />
