import { Component, Input } from '@angular/core';
/**
 * This component provides an input field with a specified mask pattern and shape.
 */
@Component({
  selector: 'input-mask',
  templateUrl: './input-mask.component.html',
  styleUrls: ['./input-mask.component.css']
})
export class InputMaskComponent {
   /**
   * The mask pattern for input validation.
   */
  @Input() maskPattern!: string; 

  /**
   * The shape of the input field (e.g., '(000-000)' or '000').
   */
  @Input() maskShape!: string;


  customPatterns = { '0': { pattern: new RegExp(this.maskPattern)} };
}
<input type="text" [patterns]="customPatterns" [mask]="maskShape"/>
