import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Customized-Masked-Input';
  phoneMaskPattern = '\\d{3}-\\d{3}-\\d{4}'; // Example phone number pattern
  phoneNumber: string =''
}
<div>
  <h1>Angular Input Mask with Regex Validation</h1>
  <input appInputMask id="customInput"  placeholder="Enter data" />
</div>
